package basicjav.aprogramfor;

public class StarProghram2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		*****
//		****
//		***
//		**
//		*
//		
		for(int i=1;i<=5;i=i+1) {
			
			for(int j=5;j>=i;j=j-1) {
				System.out.print("#");
			}
			System.out.println();
		}
		
		
		
		
		
		
		
		
	}

}
